// SPDX-License-Identifier: AGPL-3.0-only
// Ported from https://github.com/UMAprotocol/protocol/blob/%40uma/core%402.62.0/packages/core/contracts/data-verification-mechanism/interfaces/StoreInterface.sol
// to use FixedPointInterface.Unsigned as the FixedPoint library is not compatible with OpenZeppelin v5.x. This also
// uses named imports.

pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {FixedPointInterface} from "../../common/interfaces/FixedPointInterface.sol";

/**
 * @title Interface that allows financial contracts to pay oracle fees for their use of the system.
 */
interface StoreInterface {
    /**
     * @notice Pays Oracle fees in ETH to the store.
     * @dev To be used by contracts whose margin currency is ETH.
     */
    function payOracleFees() external payable;

    /**
     * @notice Pays oracle fees in the margin currency, erc20Address, to the store.
     * @dev To be used if the margin currency is an ERC20 token rather than ETH.
     * @param erc20Address address of the ERC20 token used to pay the fee.
     * @param amount number of tokens to transfer. An approval for at least this amount must exist.
     */
    function payOracleFeesErc20(address erc20Address, FixedPointInterface.Unsigned calldata amount) external;

    /**
     * @notice Computes the regular oracle fees that a contract should pay for a period.
     * @param startTime defines the beginning time from which the fee is paid.
     * @param endTime end time until which the fee is paid.
     * @param pfc "profit from corruption", or the maximum amount of margin currency that a
     * token sponsor could extract from the contract through corrupting the price feed in their favor.
     * @return regularFee amount owed for the duration from start to end time for the given pfc.
     * @return latePenalty for paying the fee after the deadline.
     */
    function computeRegularFee(uint256 startTime, uint256 endTime, FixedPointInterface.Unsigned calldata pfc)
        external
        view
        returns (FixedPointInterface.Unsigned memory regularFee, FixedPointInterface.Unsigned memory latePenalty);

    /**
     * @notice Computes the final oracle fees that a contract should pay at settlement.
     * @param currency token used to pay the final fee.
     * @return finalFee amount due.
     */
    function computeFinalFee(address currency) external view returns (FixedPointInterface.Unsigned memory);
}

src/optimistic-oracle-v2/implementation/.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.27;

import {ERC165Checker} from "@openzeppelin/contracts/utils/introspection/ERC165Checker.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {AddressWhitelistInterface} from "../../common/interfaces/AddressWhitelistInterface.sol";
import {ManagedOptimisticOracleV2Interface} from "../interfaces/ManagedOptimisticOracleV2Interface.sol";
import {MultiCaller} from "../../common/implementation/MultiCaller.sol";
import {OptimisticOracleV2} from "./OptimisticOracleV2.sol";

/**
 * @title Managed Optimistic Oracle V2.
 * @notice Pre-DVM escalation contract that allows faster settlement and management of price requests.
 * @custom:security-contact bugs@umaproject.org
 */
contract ManagedOptimisticOracleV2 is ManagedOptimisticOracleV2Interface, OptimisticOracleV2, MultiCaller {
    struct BondRange {
        uint128 minimumBond;
        uint128 maximumBond;
    }

    struct CurrencyBondRange {
        IERC20 currency;
        BondRange range;
    }

    struct CustomBond {
        uint256 amount;
        bool isSet;
    }

    struct CustomLiveness {
        uint256 liveness;
        bool isSet;
    }

    // Config admin role is used to manage request managers and set other default parameters.
    bytes32 public constant CONFIG_ADMIN_ROLE = keccak256("CONFIG_ADMIN_ROLE");

    // Request manager role is used to manage proposer whitelists, bonds, and liveness for individual requests.
    bytes32 public constant REQUEST_MANAGER_ROLE = keccak256("REQUEST_MANAGER_ROLE");

    // Default whitelist for proposers.
    AddressWhitelistInterface public defaultProposerWhitelist;
    AddressWhitelistInterface public requesterWhitelist;

    // Custom bonds set by request managers for specific request and currency combinations.
    mapping(bytes32 => mapping(IERC20 => CustomBond)) public customBonds;

    // Custom liveness values set by request managers for specific requests.
    mapping(bytes32 => CustomLiveness) public customLivenessValues;

    // Custom proposer whitelists set by request managers for specific requests.
    mapping(bytes32 => AddressWhitelistInterface) public customProposerWhitelists;

    // Admin controlled ranges limiting the changes that can be made by request managers.
    mapping(IERC20 => BondRange) public allowedBondRanges;

    // Admin controlled minimum liveness that can be set by request managers.
    uint256 public minimumLiveness;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializer.
     * @param _defaultLiveness applied to each price request.
     * @param _finderAddress to use to get addresses of DVM contracts.
     * @param _defaultProposerWhitelist address of the default whitelist.
     * @param _requesterWhitelist address of the requester whitelist.
     * @param _allowedBondRanges array of allowed bond ranges for different currencies.
     * @param _minimumLiveness that can be overridden for a request.
     * @param configAdmin address, which is used for managing request managers and contract parameters.
     * @param upgradeAdmin address, which also can manage the config admin role.
     */
    function initialize(
        uint256 _defaultLiveness,
        address _finderAddress,
        address _defaultProposerWhitelist,
        address _requesterWhitelist,
        CurrencyBondRange[] calldata _allowedBondRanges,
        uint256 _minimumLiveness,
        address configAdmin,
        address upgradeAdmin
    ) external initializer {
        __OptimisticOracleV2_init(_defaultLiveness, _finderAddress, upgradeAdmin);

        // Config admin is managing the request manager role.
        // Contract upgrade admin retains the default admin role that can also manage the config admin role.
        _grantRole(CONFIG_ADMIN_ROLE, configAdmin);
        _setRoleAdmin(REQUEST_MANAGER_ROLE, CONFIG_ADMIN_ROLE);

        _setDefaultProposerWhitelist(_defaultProposerWhitelist);
        _setRequesterWhitelist(_requesterWhitelist);
        for (uint256 i = 0; i < _allowedBondRanges.length; i++) {
            _setAllowedBondRange(_allowedBondRanges[i].currency, _allowedBondRanges[i].range);
        }
        _setMinimumLiveness(_minimumLiveness);
    }

    /**
     * @dev Throws if called by any account other than the config admin.
     */
    modifier onlyConfigAdmin() {
        _checkRole(CONFIG_ADMIN_ROLE);
        _;
    }

    /**
     * @dev Throws if called by any account other than the request manager.
     */
    modifier onlyRequestManager() {
        _checkRole(REQUEST_MANAGER_ROLE);
        _;
    }

    /**
     * @notice Adds a request manager.
     * @dev Only callable by the config admin (checked in grantRole of AccessControlUpgradeable).
     * @param requestManager address of the request manager to set.
     */
    function addRequestManager(address requestManager) external nonReentrant {
        grantRole(REQUEST_MANAGER_ROLE, requestManager);
        emit RequestManagerAdded(requestManager);
    }

    /**
     * @notice Removes a request manager.
     * @dev Only callable by the config admin (checked in revokeRole of AccessControlUpgradeable).
     * @param requestManager address of the request manager to remove.
     */
    function removeRequestManager(address requestManager) external nonReentrant {
        revokeRole(REQUEST_MANAGER_ROLE, requestManager);
        emit RequestManagerRemoved(requestManager);
    }

    /**
     * @notice Sets the bounds for a bond that can be set for a request.
     * @dev This can be used to limit the bond amount that can be set by request managers, callable by the config admin.
     * @param currency the ERC20 token used for bonding proposals and disputes. Must be approved for use with the DVM.
     * @param newRange new allowed range for the bond.
     */
    function setAllowedBondRange(IERC20 currency, BondRange calldata newRange) external nonReentrant onlyConfigAdmin {
        _setAllowedBondRange(currency, newRange);
    }

    /**
     * @notice Sets the minimum liveness that can be set for a request.
     * @dev This can be used to limit the liveness period that can be set by request managers, callable by the config admin.
     * @param _minimumLiveness new minimum liveness period.
     */
    function setMinimumLiveness(uint256 _minimumLiveness) external nonReentrant onlyConfigAdmin {
        _setMinimumLiveness(_minimumLiveness);
    }

    /**
     * @notice Sets the default proposer whitelist.
     * @dev Only callable by the config admin.
     * @param whitelist address of the whitelist to set.
     */
    function setDefaultProposerWhitelist(address whitelist) external nonReentrant onlyConfigAdmin {
        _setDefaultProposerWhitelist(whitelist);
    }

    /**
     * @notice Sets the requester whitelist.
     * @dev Only callable by the config admin.
     * @param whitelist address of the whitelist to set.
     */
    function setRequesterWhitelist(address whitelist) external nonReentrant onlyConfigAdmin {
        _setRequesterWhitelist(whitelist);
    }

    /**
     * @notice Requests a new price.
     * @param identifier price identifier being requested.
     * @param timestamp timestamp of the price being requested.
     * @param ancillaryData ancillary data representing additional args being passed with the price request.
     * @param currency ERC20 token used for payment of rewards and fees. Must be approved for use with the DVM.
     * @param reward reward offered to a successful proposer. Will be pulled from the caller. Note: this can be 0,
     *               which could make sense if the contract requests and proposes the value in the same call or
     *               provides its own reward system.
     * @return totalBond default bond (final fee) + final fee that the proposer and disputer will be required to pay.
     * This can be changed with a subsequent call to setBond().
     */
    function requestPrice(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        IERC20 currency,
        uint256 reward
    ) public override returns (uint256 totalBond) {
        require(requesterWhitelist.isOnWhitelist(msg.sender), RequesterNotWhitelisted());
        return super.requestPrice(identifier, timestamp, ancillaryData, currency, reward);
    }

    /**
     * @notice Set the proposal bond associated with a price request.
     * @dev This would also override any subsequent calls to setBond() by the requester.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param currency ERC20 token used for payment of rewards and fees. Must be approved for use with the DVM.
     * @param bond custom bond amount to set.
     */
    function requestManagerSetBond(
        address requester,
        bytes32 identifier,
        bytes memory ancillaryData,
        IERC20 currency,
        uint256 bond
    ) external nonReentrant onlyRequestManager {
        require(_getCollateralWhitelist().isOnWhitelist(address(currency)), UnsupportedCurrency());
        _validateBond(currency, bond);
        bytes32 managedRequestId = getManagedRequestId(requester, identifier, ancillaryData);
        customBonds[managedRequestId][currency] = CustomBond({amount: bond, isSet: true});
        emit CustomBondSet(managedRequestId, requester, identifier, ancillaryData, currency, bond);
    }

    /**
     * @notice Sets a custom liveness value for the request. Liveness is the amount of time a proposal must wait before
     * being auto-resolved.
     * @dev This would also override any subsequent calls to setLiveness() by the requester.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param customLiveness new custom liveness.
     */
    function requestManagerSetCustomLiveness(
        address requester,
        bytes32 identifier,
        bytes memory ancillaryData,
        uint256 customLiveness
    ) external nonReentrant onlyRequestManager {
        _validateLiveness(customLiveness);
        bytes32 managedRequestId = getManagedRequestId(requester, identifier, ancillaryData);
        customLivenessValues[managedRequestId] = CustomLiveness({liveness: customLiveness, isSet: true});
        emit CustomLivenessSet(managedRequestId, requester, identifier, ancillaryData, customLiveness);
    }

    /**
     * @notice Sets the proposer whitelist for a request.
     * @dev This can also be set in advance of the request as the timestamp is omitted from the mapping key derivation.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param whitelist address of the whitelist to set.
     */
    function requestManagerSetProposerWhitelist(
        address requester,
        bytes32 identifier,
        bytes memory ancillaryData,
        address whitelist
    ) external nonReentrant onlyRequestManager {
        // Zero address is allowed to disable the custom proposer whitelist.
        if (whitelist != address(0)) {
            _validateWhitelistInterface(whitelist);
        }
        bytes32 managedRequestId = getManagedRequestId(requester, identifier, ancillaryData);
        customProposerWhitelists[managedRequestId] = AddressWhitelistInterface(whitelist);
        emit CustomProposerWhitelistSet(managedRequestId, requester, identifier, ancillaryData, whitelist);
    }

    /**
     * @notice Proposes a price value on another address' behalf. Note: this address will receive any rewards that come
     * from this proposal. However, any bonds are pulled from the caller.
     * @param proposer address to set as the proposer.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param proposedPrice price being proposed.
     * @return totalBond the amount that's pulled from the caller's wallet as a bond. The bond will be returned to
     * the proposer once settled if the proposal is correct.
     */
    function proposePriceFor(
        address proposer,
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 proposedPrice
    ) public override returns (uint256 totalBond) {
        // Apply the custom bond and liveness overrides if set.
        Request storage request = _getRequest(requester, identifier, timestamp, ancillaryData);
        bytes32 managedRequestId = getManagedRequestId(requester, identifier, ancillaryData);
        if (customBonds[managedRequestId][request.currency].isSet) {
            request.requestSettings.bond = customBonds[managedRequestId][request.currency].amount;
        }
        if (customLivenessValues[managedRequestId].isSet) {
            request.requestSettings.customLiveness = customLivenessValues[managedRequestId].liveness;
        }

        AddressWhitelistInterface whitelist = _getEffectiveProposerWhitelist(requester, identifier, ancillaryData);

        require(whitelist.isOnWhitelist(proposer), ProposerNotWhitelisted());
        require(whitelist.isOnWhitelist(msg.sender), SenderNotWhitelisted());
        return super.proposePriceFor(proposer, requester, identifier, timestamp, ancillaryData, proposedPrice);
    }

    /**
     * @notice Gets the custom proposer whitelist for a request.
     * @dev This omits the timestamp from the key derivation, so the whitelist might have been set in advance of the
     * request.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return AddressWhitelistInterface the custom proposer whitelist for the request or zero address if not set.
     */
    function getCustomProposerWhitelist(address requester, bytes32 identifier, bytes memory ancillaryData)
        external
        view
        returns (AddressWhitelistInterface)
    {
        return customProposerWhitelists[getManagedRequestId(requester, identifier, ancillaryData)];
    }

    /**
     * @notice Returns the proposer whitelist and whether whitelist is enabled for a given request.
     * @dev If no custom proposer whitelist is set for the request, the default proposer whitelist is used.
     * If whitelist used is DisabledAddressWhitelist, the returned proposer list will be empty and isEnabled will be false,
     * indicating that any address is allowed to propose.
     * @param requester The address that made or will make the price request.
     * @param identifier The identifier of the price request.
     * @param ancillaryData Additional data used to uniquely identify the request.
     * @return allowedProposers The list of addresses allowed to propose, if whitelist is enabled. Otherwise, an empty array.
     * @return isEnabled A boolean indicating whether whitelist is enabled for this request.
     */
    function getProposerWhitelistWithEnabledStatus(address requester, bytes32 identifier, bytes memory ancillaryData)
        external
        view
        returns (address[] memory allowedProposers, bool isEnabled)
    {
        AddressWhitelistInterface whitelist = _getEffectiveProposerWhitelist(requester, identifier, ancillaryData);
        return (whitelist.getWhitelist(), whitelist.isWhitelistEnabled());
    }

    /**
     * @notice Gets the ID for a managed request.
     * @dev This omits the timestamp from the key derivation, so it can be used for managed requests in advance.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return bytes32 the ID for the managed request.
     */
    function getManagedRequestId(address requester, bytes32 identifier, bytes memory ancillaryData)
        public
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(requester, identifier, ancillaryData));
    }

    /**
     * @notice Sets the bounds for a bond that can be set for a request.
     * @dev This can be used to limit the bond amount that can be set by request managers. Setting the minimum and
     * maximum both to 0 effectively blocks the request manager from overriding the bond for a given currency.
     * @param currency the ERC20 token used for bonding proposals and disputes. Must be approved for use with the DVM.
     * @param newRange new allowed range for the bond.
     */
    function _setAllowedBondRange(IERC20 currency, BondRange calldata newRange) internal {
        require(_getCollateralWhitelist().isOnWhitelist(address(currency)), UnsupportedCurrency());
        require(newRange.minimumBond <= newRange.maximumBond, MinimumBondAboveMaximumBond());
        allowedBondRanges[currency] = newRange;
        emit AllowedBondRangeUpdated(currency, newRange.minimumBond, newRange.maximumBond);
    }

    /**
     * @notice Sets the minimum liveness that can be set for a request.
     * @dev This can be used to limit the liveness period that can be set by request managers.
     * @param _minimumLiveness new minimum liveness period.
     */
    function _setMinimumLiveness(uint256 _minimumLiveness) internal {
        super._validateLiveness(_minimumLiveness);
        minimumLiveness = _minimumLiveness;
        emit MinimumLivenessUpdated(_minimumLiveness);
    }

    /**
     * @notice Sets the default proposer whitelist.
     * @param whitelist address of the whitelist to set.
     */
    function _setDefaultProposerWhitelist(address whitelist) internal {
        _validateWhitelistInterface(whitelist);
        defaultProposerWhitelist = AddressWhitelistInterface(whitelist);
        emit DefaultProposerWhitelistUpdated(whitelist);
    }

    /**
     * @notice Sets the requester whitelist.
     * @param whitelist address of the whitelist to set.
     */
    function _setRequesterWhitelist(address whitelist) internal {
        _validateWhitelistInterface(whitelist);
        requesterWhitelist = AddressWhitelistInterface(whitelist);
        emit RequesterWhitelistUpdated(whitelist);
    }

    /**
     * @notice Validates that the given address implements the AddressWhitelistInterface.
     * @dev Reverts if the address does not implement the interface.
     * @param whitelist address of the whitelist to validate.
     */
    function _validateWhitelistInterface(address whitelist) internal view {
        require(
            ERC165Checker.supportsInterface(whitelist, type(AddressWhitelistInterface).interfaceId),
            UnsupportedWhitelistInterface()
        );
    }

    /**
     * @notice Validates the bond amount.
     * @dev Reverts if the bond is outside the allowed bond range (controllable by the config admin) or it is 0 (which
     * makes sure the allowed bond range was explicitly set).
     * @param currency the ERC20 token used for bonding proposals and disputes. Must be approved for use with the DVM.
     * @param bond the bond amount to validate.
     */
    function _validateBond(IERC20 currency, uint256 bond) internal view {
        require(bond != 0, ZeroBondNotAllowed());
        BondRange memory allowedRange = allowedBondRanges[currency];
        require(bond >= uint256(allowedRange.minimumBond), BondBelowMinimumBond());
        require(bond <= uint256(allowedRange.maximumBond), BondExceedsMaximumBond());
    }

    /**
     * @notice Validates the liveness period.
     * @dev Reverts if the liveness period is less than the minimum liveness (controllable by the config admin) or
     * above the maximum liveness (which is set in the parent contract).
     * @param liveness the liveness period to validate.
     */
    function _validateLiveness(uint256 liveness) internal view override {
        require(liveness >= minimumLiveness, LivenessTooLow());
        super._validateLiveness(liveness);
    }

    /**
     * @notice Gets the effective proposer whitelist contract for a given request.
     * @dev Returns the custom proposer whitelist if set; otherwise falls back to the default. Timestamp is omitted from
     * the key derivation, so this can be used for checks before the request is made.
     * @param requester The address that made or will make the price request.
     * @param identifier The identifier of the price request.
     * @param ancillaryData Additional data used to uniquely identify the request.
     * @return whitelist The effective AddressWhitelistInterface for the request.
     */
    function _getEffectiveProposerWhitelist(address requester, bytes32 identifier, bytes memory ancillaryData)
        internal
        view
        returns (AddressWhitelistInterface whitelist)
    {
        whitelist = customProposerWhitelists[getManagedRequestId(requester, identifier, ancillaryData)];
        if (address(whitelist) == address(0)) {
            whitelist = defaultProposerWhitelist;
        }
    }

    /**
     * @dev Reserve storage slots for future versions of this base contract to add state variables without affecting the
     * storage layout of child contracts. Decrement the size of __gap whenever state variables are added. This is at the
     * bottom of contract to make sure its always at the end of storage.
     * See https://docs.openzeppelin.com/upgrades-plugins/writing-upgradeable#storage-gaps
     */
    uint256[993] private __gap;
}

// SPDX-License-Identifier: AGPL-3.0-only
// Ported from https://github.com/UMAprotocol/protocol/blob/%40uma/core%402.62.0/packages/core/contracts/optimistic-oracle-v2/implementation/OptimisticOracleV2.sol
// to be compatible for use in upgradeable contracts and OpenZeppelin v5.x. This also uses custom errors, named imports
// and linting from Foundry.

pragma solidity ^0.8.27;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {AccessControlDefaultAdminRulesUpgradeable} from
    "@openzeppelin/contracts-upgradeable/access/extensions/AccessControlDefaultAdminRulesUpgradeable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import {AncillaryData} from "@uma/contracts/common/implementation/AncillaryData.sol";
import {FinderInterface} from "@uma/contracts/data-verification-mechanism/interfaces/FinderInterface.sol";
import {IdentifierWhitelistInterface} from
    "@uma/contracts/data-verification-mechanism/interfaces/IdentifierWhitelistInterface.sol";
import {OracleAncillaryInterface} from
    "@uma/contracts/data-verification-mechanism/interfaces/OracleAncillaryInterface.sol";
import {OracleInterfaces} from "@uma/contracts/data-verification-mechanism/implementation/Constants.sol";

import {OptimisticOracleV2Interface} from "../interfaces/OptimisticOracleV2Interface.sol";

import {AddressLegacy} from "../../common/implementation/AddressLegacy.sol";
import {AddressWhitelist} from "../../common/implementation/AddressWhitelist.sol";
import {FixedPointInterface} from "../../common/interfaces/FixedPointInterface.sol";
import {LockableUpgradeable} from "../../common/implementation/LockableUpgradeable.sol";
import {StoreInterface} from "../../data-verification-mechanism/interfaces/StoreInterface.sol";

/**
 * @title Optimistic Requester.
 * @notice Optional interface that requesters can implement to receive callbacks.
 * @dev this contract does _not_ work with ERC777 collateral currencies or any others that call into the receiver on
 * transfer(). Using an ERC777 token would allow a user to maliciously grief other participants (while also losing
 * money themselves).
 */
interface OptimisticRequester {
    /**
     * @notice Callback for proposals.
     * @param identifier price identifier being requested.
     * @param timestamp timestamp of the price being requested.
     * @param ancillaryData ancillary data of the price being requested.
     */
    function priceProposed(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData) external;

    /**
     * @notice Callback for disputes.
     * @param identifier price identifier being requested.
     * @param timestamp timestamp of the price being requested.
     * @param ancillaryData ancillary data of the price being requested.
     * @param refund refund received in the case that refundOnDispute was enabled.
     */
    function priceDisputed(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData, uint256 refund)
        external;

    /**
     * @notice Callback for settlement.
     * @param identifier price identifier being requested.
     * @param timestamp timestamp of the price being requested.
     * @param ancillaryData ancillary data of the price being requested.
     * @param price price that was resolved by the escalation process.
     */
    function priceSettled(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData, int256 price) external;
}

/**
 * @title Optimistic Oracle.
 * @notice Pre-DVM escalation contract that allows faster settlement.
 * @custom:security-contact bugs@umaproject.org
 */
contract OptimisticOracleV2 is
    OptimisticOracleV2Interface,
    UUPSUpgradeable,
    AccessControlDefaultAdminRulesUpgradeable,
    LockableUpgradeable
{
    using SafeERC20 for IERC20;
    using AddressLegacy for address;

    bytes32 public constant UPGRADE_ADMIN_ROLE = DEFAULT_ADMIN_ROLE;

    // Finder to provide addresses for DVM contracts.
    FinderInterface public override finder;

    // Default liveness value for all price requests.
    uint256 public override defaultLiveness;

    // This is effectively the extra ancillary data to add ",ooRequester:0000000000000000000000000000000000000000".
    uint256 private constant MAX_ADDED_ANCILLARY_DATA = 53;
    uint256 public constant OO_ANCILLARY_DATA_LIMIT = ancillaryBytesLimit - MAX_ADDED_ANCILLARY_DATA;
    int256 public constant TOO_EARLY_RESPONSE = type(int256).min;

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializer (external).
     * @dev Used only for standalone deployments of the OptimisticOracleV2Upgradeable contract.
     * @param _liveness default liveness applied to each price request.
     * @param _finderAddress finder to use to get addresses of DVM contracts.
     */
    function initialize(uint256 _liveness, address _finderAddress, address upgradeAdmin) external initializer {
        __OptimisticOracleV2_init(_liveness, _finderAddress, upgradeAdmin);
    }

    /**
     * @notice Initializer (internal, main entry point).
     * @param _liveness default liveness applied to each price request.
     * @param _finderAddress finder to use to get addresses of DVM contracts.
     */
    function __OptimisticOracleV2_init(uint256 _liveness, address _finderAddress, address upgradeAdmin)
        internal
        onlyInitializing
    {
        __UUPSUpgradeable_init();
        __AccessControlDefaultAdminRules_init(3 days, upgradeAdmin); // Initialize `DEFAULT_ADMIN_ROLE`, and by extension, `UPGRADE_ADMIN_ROLE`
        __LockableUpgradeable_init();
        __OptimisticOracleV2_init_unchained(_liveness, _finderAddress);
    }

    /**
     * @notice Initializer (internal, unchained).
     * @param _liveness default liveness applied to each price request.
     * @param _finderAddress finder to use to get addresses of DVM contracts.
     */
    function __OptimisticOracleV2_init_unchained(uint256 _liveness, address _finderAddress) internal onlyInitializing {
        finder = FinderInterface(_finderAddress);
        _validateLiveness(_liveness);
        defaultLiveness = _liveness;
    }

    /**
     * @dev Throws if called by any account other than the upgrade admin.
     */
    modifier onlyUpgradeAdmin() {
        _checkRole(UPGRADE_ADMIN_ROLE);
        _;
    }

    /**
     * @notice Authorizes the upgrade of the contract.
     * @dev This is required for UUPSUpgradeable. Only the upgrade admin can authorize upgrades.
     * @param newImplementation address of the new implementation to upgrade to.
     */
    function _authorizeUpgrade(address newImplementation) internal override onlyUpgradeAdmin {}

    /**
     * @notice Requests a new price.
     * @param identifier price identifier being requested.
     * @param timestamp timestamp of the price being requested.
     * @param ancillaryData ancillary data representing additional args being passed with the price request.
     * @param currency ERC20 token used for payment of rewards and fees. Must be approved for use with the DVM.
     * @param reward reward offered to a successful proposer. Will be pulled from the caller. Note: this can be 0,
     *               which could make sense if the contract requests and proposes the value in the same call or
     *               provides its own reward system.
     * @return totalBond default bond (final fee) + final fee that the proposer and disputer will be required to pay.
     * This can be changed with a subsequent call to setBond().
     */
    function requestPrice(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        IERC20 currency,
        uint256 reward
    ) public virtual override nonReentrant returns (uint256 totalBond) {
        require(_getState(msg.sender, identifier, timestamp, ancillaryData) == State.Invalid, RequestStateNotInvalid());
        require(_getIdentifierWhitelist().isIdentifierSupported(identifier), UnsupportedIdentifier());
        require(_getCollateralWhitelist().isOnWhitelist(address(currency)), UnsupportedCurrency());
        require(timestamp <= getCurrentTime(), TimestampInFuture());

        // This ensures that the ancillary data is <= the OO limit, which is lower than the DVM limit because the
        // OO adds some data before sending to the DVM.
        require(ancillaryData.length <= OO_ANCILLARY_DATA_LIMIT, AncillaryDataTooLong());

        uint256 finalFee = _getStore().computeFinalFee(address(currency)).rawValue;
        requests[_getId(msg.sender, identifier, timestamp, ancillaryData)] = Request({
            proposer: address(0),
            disputer: address(0),
            currency: currency,
            settled: false,
            requestSettings: RequestSettings({
                eventBased: false,
                refundOnDispute: false,
                callbackOnPriceProposed: false,
                callbackOnPriceDisputed: false,
                callbackOnPriceSettled: false,
                bond: finalFee,
                customLiveness: 0
            }),
            proposedPrice: 0,
            resolvedPrice: 0,
            expirationTime: 0,
            reward: reward,
            finalFee: finalFee
        });

        if (reward > 0) {
            currency.safeTransferFrom(msg.sender, address(this), reward);
        }

        emit RequestPrice(msg.sender, identifier, timestamp, ancillaryData, address(currency), reward, finalFee);

        // This function returns the initial proposal bond for this request, which can be customized by calling
        // setBond() with the same identifier and timestamp.
        return finalFee * 2;
    }

    /**
     * @notice Set the proposal bond associated with a price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param bond custom bond amount to set.
     * @return totalBond new bond + final fee that the proposer and disputer will be required to pay. This can be
     * changed again with a subsequent call to setBond().
     */
    function setBond(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData, uint256 bond)
        external
        override
        nonReentrant
        returns (uint256 totalBond)
    {
        require(
            _getState(msg.sender, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        Request storage request = _getRequest(msg.sender, identifier, timestamp, ancillaryData);
        request.requestSettings.bond = bond;

        // Total bond is the final fee + the newly set bond.
        return bond + request.finalFee;
    }

    /**
     * @notice Sets the request to refund the reward if the proposal is disputed. This can help to "hedge" the caller
     * in the event of a dispute-caused delay. Note: in the event of a dispute, the winner still receives the other's
     * bond, so there is still profit to be made even if the reward is refunded.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     */
    function setRefundOnDispute(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        external
        override
        nonReentrant
    {
        require(
            _getState(msg.sender, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        _getRequest(msg.sender, identifier, timestamp, ancillaryData).requestSettings.refundOnDispute = true;
    }

    /**
     * @notice Sets a custom liveness value for the request. Liveness is the amount of time a proposal must wait before
     * being auto-resolved.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param customLiveness new custom liveness.
     */
    function setCustomLiveness(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        uint256 customLiveness
    ) external override nonReentrant {
        require(
            _getState(msg.sender, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        _validateLiveness(customLiveness);
        _getRequest(msg.sender, identifier, timestamp, ancillaryData).requestSettings.customLiveness = customLiveness;
    }

    /**
     * @notice Sets the request to be an "event-based" request.
     * @dev Calling this method has a few impacts on the request:
     *
     * 1. The timestamp at which the request is evaluated is the time of the proposal, not the timestamp associated
     *    with the request.
     *
     * 2. The proposer cannot propose the "too early" value (TOO_EARLY_RESPONSE). This is to ensure that a proposer who
     *    prematurely proposes a response loses their bond.
     *
     * 3. RefundoOnDispute is automatically set, meaning disputes trigger the reward to be automatically refunded to
     *    the requesting contract.
     *
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     */
    function setEventBased(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        external
        override
        nonReentrant
    {
        require(
            _getState(msg.sender, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        Request storage request = _getRequest(msg.sender, identifier, timestamp, ancillaryData);
        request.requestSettings.eventBased = true;
        request.requestSettings.refundOnDispute = true;
    }

    /**
     * @notice Sets which callbacks should be enabled for the request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param callbackOnPriceProposed whether to enable the callback onPriceProposed.
     * @param callbackOnPriceDisputed whether to enable the callback onPriceDisputed.
     * @param callbackOnPriceSettled whether to enable the callback onPriceSettled.
     */
    function setCallbacks(
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        bool callbackOnPriceProposed,
        bool callbackOnPriceDisputed,
        bool callbackOnPriceSettled
    ) external override nonReentrant {
        require(
            _getState(msg.sender, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        Request storage request = _getRequest(msg.sender, identifier, timestamp, ancillaryData);
        request.requestSettings.callbackOnPriceProposed = callbackOnPriceProposed;
        request.requestSettings.callbackOnPriceDisputed = callbackOnPriceDisputed;
        request.requestSettings.callbackOnPriceSettled = callbackOnPriceSettled;
    }

    /**
     * @notice Proposes a price value on another address' behalf. Note: this address will receive any rewards that come
     * from this proposal. However, any bonds are pulled from the caller.
     * @param proposer address to set as the proposer.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param proposedPrice price being proposed.
     * @return totalBond the amount that's pulled from the caller's wallet as a bond. The bond will be returned to
     * the proposer once settled if the proposal is correct.
     */
    function proposePriceFor(
        address proposer,
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 proposedPrice
    ) public virtual override nonReentrant returns (uint256 totalBond) {
        require(proposer != address(0), ProposerAddressCannotBeZero());
        require(
            _getState(requester, identifier, timestamp, ancillaryData) == State.Requested, RequestStateNotRequested()
        );
        Request storage request = _getRequest(requester, identifier, timestamp, ancillaryData);
        if (request.requestSettings.eventBased) {
            require(proposedPrice != TOO_EARLY_RESPONSE, CannotProposeTooEarly());
        }
        request.proposer = proposer;
        request.proposedPrice = proposedPrice;

        // If a custom liveness has been set, use it instead of the default.
        request.expirationTime = getCurrentTime()
            + (request.requestSettings.customLiveness != 0 ? request.requestSettings.customLiveness : defaultLiveness);

        totalBond = request.requestSettings.bond + request.finalFee;
        if (totalBond > 0) request.currency.safeTransferFrom(msg.sender, address(this), totalBond);

        emit ProposePrice(
            requester,
            proposer,
            identifier,
            timestamp,
            ancillaryData,
            proposedPrice,
            request.expirationTime,
            address(request.currency)
        );

        // End the re-entrancy guard early to allow the caller to potentially take OO-related actions inside this callback.
        _startReentrantGuardDisabled();
        // Callback.
        if (address(requester).isContract() && request.requestSettings.callbackOnPriceProposed) {
            OptimisticRequester(requester).priceProposed(identifier, timestamp, ancillaryData);
        }
        _endReentrantGuardDisabled();
    }

    /**
     * @notice Proposes a price value for an existing price request.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @param proposedPrice price being proposed.
     * @return totalBond the amount that's pulled from the proposer's wallet as a bond. The bond will be returned to
     * the proposer once settled if the proposal is correct.
     */
    function proposePrice(
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData,
        int256 proposedPrice
    ) external override returns (uint256 totalBond) {
        // Note: re-entrancy guard is done in the inner call.
        return proposePriceFor(msg.sender, requester, identifier, timestamp, ancillaryData, proposedPrice);
    }

    /**
     * @notice Disputes a price request with an active proposal on another address' behalf. Note: this address will
     * receive any rewards that come from this dispute. However, any bonds are pulled from the caller.
     * @param disputer address to set as the disputer.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return totalBond the amount that's pulled from the caller's wallet as a bond. The bond will be returned to
     * the disputer once settled if the dispute was valid (the proposal was incorrect).
     */
    function disputePriceFor(
        address disputer,
        address requester,
        bytes32 identifier,
        uint256 timestamp,
        bytes memory ancillaryData
    ) public override nonReentrant returns (uint256 totalBond) {
        require(disputer != address(0), DisputerAddressCannotBeZero());
        require(_getState(requester, identifier, timestamp, ancillaryData) == State.Proposed, RequestStateNotProposed());
        Request storage request = _getRequest(requester, identifier, timestamp, ancillaryData);
        request.disputer = disputer;

        uint256 finalFee = request.finalFee;
        uint256 bond = request.requestSettings.bond;
        totalBond = bond + finalFee;
        if (totalBond > 0) {
            request.currency.safeTransferFrom(msg.sender, address(this), totalBond);
        }

        StoreInterface store = _getStore();

        // Along with the final fee, "burn" part of the loser's bond to ensure that a larger bond always makes it
        // proportionally more expensive to delay the resolution even if the proposer and disputer are the same
        // party.

        // The total fee is the burned bond and the final fee added together.
        uint256 totalFee = finalFee + _computeBurnedBond(request);
        if (totalFee > 0) {
            request.currency.safeIncreaseAllowance(address(store), totalFee);
            _getStore().payOracleFeesErc20(address(request.currency), FixedPointInterface.Unsigned(totalFee));
        }

        _getOracle().requestPrice(
            identifier, _getTimestampForDvmRequest(request, timestamp), _stampAncillaryData(ancillaryData, requester)
        );

        // Compute refund.
        uint256 refund = 0;
        if (request.reward > 0 && request.requestSettings.refundOnDispute) {
            refund = request.reward;
            request.reward = 0;
            request.currency.safeTransfer(requester, refund);
        }

        emit DisputePrice(
            requester, request.proposer, disputer, identifier, timestamp, ancillaryData, request.proposedPrice
        );

        // End the re-entrancy guard early to allow the caller to potentially re-request inside this callback.
        _startReentrantGuardDisabled();
        // Callback.
        if (address(requester).isContract() && request.requestSettings.callbackOnPriceDisputed) {
            OptimisticRequester(requester).priceDisputed(identifier, timestamp, ancillaryData, refund);
        }
        _endReentrantGuardDisabled();
    }

    /**
     * @notice Disputes a price value for an existing price request with an active proposal.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return totalBond the amount that's pulled from the disputer's wallet as a bond. The bond will be returned to
     * the disputer once settled if the dispute was valid (the proposal was incorrect).
     */
    function disputePrice(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        external
        override
        returns (uint256 totalBond)
    {
        // Note: re-entrancy guard is done in the inner call.
        return disputePriceFor(msg.sender, requester, identifier, timestamp, ancillaryData);
    }

    /**
     * @notice Retrieves a price that was previously requested by a caller. Reverts if the request is not settled
     * or settleable. Note: this method is not view so that this call may actually settle the price request if it
     * hasn't been settled.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return resolved price.
     */
    function settleAndGetPrice(bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        external
        override
        nonReentrant
        returns (int256)
    {
        if (_getState(msg.sender, identifier, timestamp, ancillaryData) != State.Settled) {
            _settle(msg.sender, identifier, timestamp, ancillaryData);
        }

        return _getRequest(msg.sender, identifier, timestamp, ancillaryData).resolvedPrice;
    }

    /**
     * @notice Attempts to settle an outstanding price request. Will revert if it isn't settleable.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return payout the amount that the "winner" (proposer or disputer) receives on settlement. This amount includes
     * the returned bonds as well as additional rewards.
     */
    function settle(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        external
        override
        nonReentrant
        returns (uint256 payout)
    {
        return _settle(requester, identifier, timestamp, ancillaryData);
    }

    /**
     * @notice Gets the current data structure containing all information about a price request.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return the Request data structure.
     */
    function getRequest(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        public
        view
        override
        nonReentrantView
        returns (Request memory)
    {
        return _getRequest(requester, identifier, timestamp, ancillaryData);
    }

    /**
     * @notice Computes the current state of a price request. See the State enum for more details.
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return the State.
     */
    function getState(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        public
        view
        override
        nonReentrantView
        returns (State)
    {
        return _getState(requester, identifier, timestamp, ancillaryData);
    }

    /**
     * @notice Checks if a given request has resolved, expired or been settled (i.e the optimistic oracle has a price).
     * @param requester sender of the initial price request.
     * @param identifier price identifier to identify the existing request.
     * @param timestamp timestamp to identify the existing request.
     * @param ancillaryData ancillary data of the price being requested.
     * @return boolean indicating true if price exists and false if not.
     */
    function hasPrice(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        public
        view
        override
        nonReentrantView
        returns (bool)
    {
        State state = _getState(requester, identifier, timestamp, ancillaryData);
        return state == State.Settled || state == State.Resolved || state == State.Expired;
    }

    /**
     * @notice Generates stamped ancillary data in the format that it would be used in the case of a price dispute.
     * @param ancillaryData ancillary data of the price being requested.
     * @param requester sender of the initial price request.
     * @return the stamped ancillary bytes.
     */
    function stampAncillaryData(bytes memory ancillaryData, address requester)
        public
        pure
        override
        returns (bytes memory)
    {
        return _stampAncillaryData(ancillaryData, requester);
    }

    function _getId(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        internal
        pure
        returns (bytes32)
    {
        return keccak256(abi.encodePacked(requester, identifier, timestamp, ancillaryData));
    }

    function _settle(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        private
        returns (uint256 payout)
    {
        State state = _getState(requester, identifier, timestamp, ancillaryData);

        // Set it to settled so this function can never be entered again.
        Request storage request = _getRequest(requester, identifier, timestamp, ancillaryData);
        request.settled = true;

        if (state == State.Expired) {
            // In the expiry case, just pay back the proposer's bond and final fee along with the reward.
            request.resolvedPrice = request.proposedPrice;
            payout = request.requestSettings.bond + request.finalFee + request.reward;
            request.currency.safeTransfer(request.proposer, payout);
        } else if (state == State.Resolved) {
            // In the Resolved case, pay either the disputer or the proposer the entire payout (+ bond and reward).
            request.resolvedPrice = _getOracle().getPrice(
                identifier,
                _getTimestampForDvmRequest(request, timestamp),
                _stampAncillaryData(ancillaryData, requester)
            );
            bool disputeSuccess = request.resolvedPrice != request.proposedPrice;
            uint256 bond = request.requestSettings.bond;

            // Unburned portion of the loser's bond = 1 - burned bond.
            uint256 unburnedBond = bond - _computeBurnedBond(request);

            // Winner gets:
            // - Their bond back.
            // - The unburned portion of the loser's bond.
            // - Their final fee back.
            // - The request reward (if not already refunded -- if refunded, it will be set to 0).
            payout = bond + unburnedBond + request.finalFee + request.reward;
            request.currency.safeTransfer(disputeSuccess ? request.disputer : request.proposer, payout);
        } else {
            revert RequestNotSettleable();
        }

        emit Settle(
            requester,
            request.proposer,
            request.disputer,
            identifier,
            timestamp,
            ancillaryData,
            request.resolvedPrice,
            payout
        );

        // Temporarily disable the re-entrancy guard early to allow the caller to take an OO-related action inside this callback.
        _startReentrantGuardDisabled();
        // Callback.
        if (address(requester).isContract() && request.requestSettings.callbackOnPriceSettled) {
            OptimisticRequester(requester).priceSettled(identifier, timestamp, ancillaryData, request.resolvedPrice);
        }
        _endReentrantGuardDisabled();
    }

    function _getRequest(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        internal
        view
        returns (Request storage)
    {
        return requests[_getId(requester, identifier, timestamp, ancillaryData)];
    }

    function _computeBurnedBond(Request storage request) private view returns (uint256) {
        // burnedBond = floor(bond / 2)
        return request.requestSettings.bond / 2;
    }

    function _validateLiveness(uint256 _liveness) internal view virtual {
        require(_liveness < 5200 weeks, LivenessTooLarge());
        require(_liveness > 0, LivenessCannotBeZero());
    }

    function _getState(address requester, bytes32 identifier, uint256 timestamp, bytes memory ancillaryData)
        internal
        view
        returns (State)
    {
        Request storage request = _getRequest(requester, identifier, timestamp, ancillaryData);

        if (address(request.currency) == address(0)) return State.Invalid;

        if (request.proposer == address(0)) return State.Requested;

        if (request.settled) return State.Settled;

        if (request.disputer == address(0)) {
            return request.expirationTime <= getCurrentTime() ? State.Expired : State.Proposed;
        }

        return _getOracle().hasPrice(
            identifier, _getTimestampForDvmRequest(request, timestamp), _stampAncillaryData(ancillaryData, requester)
        ) ? State.Resolved : State.Disputed;
    }

    function _getOracle() internal view returns (OracleAncillaryInterface) {
        return OracleAncillaryInterface(finder.getImplementationAddress(OracleInterfaces.Oracle));
    }

    function _getCollateralWhitelist() internal view returns (AddressWhitelist) {
        return AddressWhitelist(finder.getImplementationAddress(OracleInterfaces.CollateralWhitelist));
    }

    function _getStore() internal view returns (StoreInterface) {
        return StoreInterface(finder.getImplementationAddress(OracleInterfaces.Store));
    }

    function _getIdentifierWhitelist() internal view returns (IdentifierWhitelistInterface) {
        return IdentifierWhitelistInterface(finder.getImplementationAddress(OracleInterfaces.IdentifierWhitelist));
    }

    function _getTimestampForDvmRequest(Request storage request, uint256 requestTimestamp)
        internal
        view
        returns (uint256)
    {
        if (request.requestSettings.eventBased) {
            uint256 liveness =
                request.requestSettings.customLiveness != 0 ? request.requestSettings.customLiveness : defaultLiveness;
            return request.expirationTime - liveness;
        } else {
            return requestTimestamp;
        }
    }

    /**
     * @dev We don't handle specifically the case where `ancillaryData` is not already readily translateable in utf8.
     * For those cases, we assume that the client will be able to strip out the utf8-translateable part of the
     * ancillary data that this contract stamps.
     */
    function _stampAncillaryData(bytes memory ancillaryData, address requester) internal pure returns (bytes memory) {
        // Since this contract will be the one to formally submit DVM price requests, its useful for voters to know who
        // the original requester was.
        return AncillaryData.appendKeyValueAddress(ancillaryData, "ooRequester", requester);
    }

    function getCurrentTime() public view override returns (uint256) {
        return block.timestamp;
    }

    /**
     * @dev Reserve storage slots for future versions of this base contract to add state variables without affecting the
     * storage layout of child contracts. Decrement the size of __gap whenever state variables are added. This is at the
     * bottom of contract to make sure its always at the end of storage.
     * See https://docs.openzeppelin.com/upgrades-plugins/writing-upgradeable#storage-gaps
     */
    uint256[998] private __gap;
}

